<template>
  <div class="customer">
    <div class="customer-content">
      <!-- đủ điều kiện đổi voucher -> màu xanh lá -->
      <img
        v-if="customer.Amount >= voucher.Condition"
        class="image"
        src="data:image/png;base64,"
        alt="customer"
      />
      <!-- Sắp đủ điều kiện đổi (>2/3) -> màu vàng -->

      <img
        v-else-if="customer.Amount / voucher.Condition >= 2 / 3"
        class="image"
        src="data:image/png;base64,"
        alt="customer"
      />
      <!-- Còn lại -> màu xám -->

      <img
        v-else
        class="image"
        src="data:image/png;base64,"
        alt="customer"
      />
      <div class="content">
        <p class=" customer--text customer--name" @click="detailVoucher">
          {{ customer.CustomerName }} - {{ customer.CustomerID }}
        </p>
        <p class="customer--text">
          Tổng tích lũy: {{ customer.Amount | toVND }}
        </p>
        <p class="customer--text" v-if="customer.ExpirationDate">
          Voucher sắp hết:
          {{ Date.parse(customer.ExpirationDate).toString("dd/MM/yyyy") }} ({{
            customer.DayLeft
          }}
          ngày)
        </p>
        <el-progress
          :text-inside="true"
          :stroke-width="20"
          :percentage="percentage"
          status="success"
        ></el-progress>
      </div>
      <!-- <div class="label-add" @click="viewTimeLine(item)">Timeline</div> -->
    </div>
    <el-dialog :visible.sync="dialogForm" custom-class="default-modal">
      <h3 class="title-dialog" slot="title">
        <i class="fas fa-receipt"></i>
        <span> {{ titleDialog }}</span>
      </h3>
      <order :voucher="voucher" :customer="customer"></order>
    </el-dialog>
  </div>
</template>

<script>
import Order from "./order-modal";

export default {
  components: { Order },
  props: ["customer", "voucher"],
  data() {
    return {
      percentage: 0,
      dialogForm: false,
      titleDialog: "",
      userName: "",
      token: ""
    };
  },
  methods: {
    detailVoucher() {
      this.dialogForm = true;
      this.titleDialog = "Chi tiết tích lũy";
    }
  },
  created() {
    this.percentage = Number(
      ((this.customer.Amount / this.voucher.Condition) * 100).toFixed(2)
    );
  },
  mounted() {},
  watch: {}
};
</script>

