<script>
import { GetResultEmployee } from "@/api/business";
import { getIdUser } from "@/utils/auth";

export default {
  props: [
    "sumAmount",
    "countCus",
    "countPro",
    "countTypeSale",
    "employee",
    "month"
  ],
  created() {
    this.fetchData();
    this.fetchSaleResult();
  },
  data() {
    return {
      items: [
        {
          value: 1,
          label: "Doanh s·ªë t·ªïng",
          amount: 120,
          sum: 0,
          unit: "vnƒë"
        },
        {
          value: 2,
          label: "Kh√°ch h√†ng",
          amount: -10,
          sum: 0
        },
        {
          value: 3,
          label: "S·∫£n ph·∫©m",
          amount: 10,
          sum: 0
        },
        {
          value: 4,
          label: "K√™nh b√°n",
          sum: 0
        }
      ],
      resultSale: []
    };
  },
  methods: {
    fetchData() {
      this.items.forEach(i => {
        switch (i.value) {
          case 1:
            i.sum = this.sumAmount;
            break;
          case 2:
            i.sum = this.countCus;
            break;
          case 3:
            i.sum = this.countPro;
            break;
          case 4:
            i.sum = this.countTypeSale;
            break;
        }
      });
    },
    fetchSaleResult() {
      const req = {
        EmployeeID: this.employee ? this.employee.EmployeeID : getIdUser(),
        Month: Date.parse(this.month).toString("yyyy-MM-dd")
      };
      GetResultEmployee(req).then(res => {
        if (res.RespCode == 0) {
          this.resultSale = res.Data;
        }
      });
    }
  },
  watch: {
    month() {
      this.fetchSaleResult();
    }
  }
};
</script>

